mapper.select:
select * from tickets


        AND state = #{state}


        AND type = #{type}


        AND belongsTo = #{belongsTo}


        AND ownerId = #{ownerId}


        AND assigneeId = #{assigneeId}


        AND id IN (SELECT ticketId FROM ticket_watcher WHERE userId = #{watcherId})

    AND belongsTo IN (SELECT categoryId FROM user_category WHERE userId = #{currentUserId})
